import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import axios from 'axios';
import './Login.css';

const Login = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [mot_passe, setMotPasse] = useState('');
  const [error, setError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
  
    const requestData = {
      email: email,
      mot_passe: mot_passe,
    };
  
    try {
      const response = await axios.post('http://localhost:8080/loginuser', requestData, {
        withCredentials: true,
      });
  
      console.log('Response from backend:', response.data);
  
      if (response && response.data && response.data.status === 'ok') {
        const user = response.data.data.user;
  
        console.log('User from response:', user);
  
        if (user) {
          // Authentification réussie
          if (user.role === 'admin') {
            console.log('Redirecting to /admin');
            navigate('/admin');
          } else if (user.role === 'user') {
            console.log('Redirecting to /dashboard');
            navigate('/dashboard');
          } else {
            console.error('Unknown role:', user.role);
          }
        } else {
          // Utilisateur non trouvé dans la réponse
          console.error('User not found in response');
          setError('Email ou mot de passe invalide');
        }
      } else {
        // Réponse inattendue du backend
        console.error('Invalid response format');
        setError('Email ou mot de passe invalide');
      }
    } catch (error) {
      // Erreur lors de la requête
      console.error('Login failed', error);
      setError('Email ou mot de passe invalide');
    }
  };
  
  
  
  const loginWithGoogle = () => {
    window.open('http://localhost:8080/auth/google/callback', '_self');
  };

  return (
    <div className="login-page">
      <h2 style={{ textAlign: 'center' }}>Connexion</h2>
      <p>veuillez vous authentifier </p>
      <div className="form">
        {error && <p style={{ color: 'red' }}>{error}</p>}
        <form className="login-form" onSubmit={handleSubmit}>
          <label>Email:</label>
          <input
            type="email"
            name="email"
            placeholder="Entrez votre Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />

          <label>Mot de passe:</label>
          <input
            type="password"
            placeholder="Entrez votre mot de passe "
            value={mot_passe}
            onChange={(e) => setMotPasse(e.target.value)}
          />

          <button type="submit">Connexion</button>
        </form>
        <p className="message">
          Vous n&apos;avez pas de compte ? <Link to="/signup">Sign Up</Link>
        </p>

        <button className="login-with-google-btn" onClick={loginWithGoogle}>
          Se connecter avec Google
        </button>
      </div>
    </div>
  );
};

export default Login;
