// SignUp.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './SignUp.css';

const SignUp = () => {
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    nom: '',
    prenom: '',
    email: '',
    mot_passe: '',
    date_naissance: '',
    Nbphone: '',
  });

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost:8080/register', formData);

      if (response.data.status === 'ok') {
        // Envoi du code de validation par e-mail après une inscription réussie
        const verificationResponse = await axios.post('http://localhost:8080/verify-email', {
          email: formData.email,
          verificationCode: response.data.verificationCode,
        });

        if (verificationResponse.data.status === 'ok') {
          navigate('/login');
        } else {
          console.error('Erreur de vérification de l\'e-mail:', verificationResponse.data.message);
        }
      } else {
        console.error('Erreur d\'inscription:', response.data.message);
      }
    } catch (error) {
      console.error('Erreur d\'inscription:', error.response ? error.response.data.message : error.message);
    }
  };

  return (
    <div className='signup_page '>
      <h2 style={{ textAlign: 'center' }}>Inscription</h2>
      <form onSubmit={handleSubmit}>
        {/* ... Vos champs de formulaire actuels */}
        <button type="submit">S'inscrire</button>
      </form>
    </div>
  );
};

export default SignUp;
