import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import axios from 'axios';
import './Login.css';

const Login = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [mot_passe, setMotPasse] = useState('');
  const [error, setError] = useState(null); // Nouvel état pour gérer les erreurs

  const handleSubmit = async (e) => {
    e.preventDefault();
  
    // Log the email and password before making the request
    console.log('Email:', email);
    console.log('Mot de passe:', mot_passe);
  
    try {
      const response = await axios.post('http://localhost:8080/loginuser', data, {
        withCredentials: true,
      })
      
      console.log('Server Response:', response.data);
  
      if (response && response.data) {
        const user = response.data.user;
        console.log('User Object:', user);
  
        if (user.role === 'admin') {
          navigate('/admin');
        } else if (user.role === 'user') {
          navigate('/dashboard'); // Mettez à jour la route en fonction de votre application
        } else {
          console.error('Unknown role:', user.role);
        }
      } else {
        console.error('Invalid response format');
        setError('Format de réponse invalide');
      }
    } catch (error) {
      console.error('Login failed', error);
      setError('Email ou mot de passe invalide');
    }
  };
  

  const loginWithGoogle = () => {
    window.open('http://localhost:8080/auth/google/callback', '_self');
  };

  return (
    <div className="login-page">
      <h2 style={{ textAlign: 'center' }}>Connexion</h2>
      <p>veuillez vous authentifier </p>
      <div className="form">
        {error && <p style={{ color: 'red' }}>{error}</p>}
        <form className="login-form" onSubmit={handleSubmit}>
          <label>Email:</label>
          <input
            type="email"
            name="email"
            placeholder="Entrez votre Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />

          <label>Mot de passe:</label>
          <input
            type="password"
            placeholder="Entrez votre mot de passe "
            value={mot_passe}
            onChange={(e) => setMotPasse(e.target.value)}
          />

          <button type="submit">Connexion</button>
        </form>
        <p className="message">
        Vous n&apos;avez pas de compte ? <Link to="/signup">Sign Up</Link>
        </p>

        <button className="login-with-google-btn" onClick={loginWithGoogle}>
          Se connecter avec Google
        </button>
      </div>
    </div>
  );
};

export default Login;
