import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import "./Login.css";
import { Link } from 'react-router-dom';
import User from "./component/User";
import Admin from "./component/Admin";

const Login = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [motPasse, setMotPasse] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();
  
    // Check if the entered email and password match the admin credentials
    if (email === 'admin@example.com' && motPasse === 'adminPassword') {
      // Admin credentials are valid, redirect to the admin page
      navigate('/admin');
      return;
    }
  
    try {
      const response = await axios.post('http://localhost:8080/api/login', { email, mot_passe }, { withCredentials: true });
      console.log('Server Response:', response.data);
  
      const user = response.data.user;
      console.log('User Object:', user);
  
      if (user.role === 'admin') {
        navigate('/admin');
      } else if (user.role === 'user') {
        navigate('/user');
      } else {
        console.error('Unknown role:', user.role);
      }
    } catch (error) {
      console.error('Login failed', error);
    }
  };
  

  const loginWithGoogle = () => {
    window.open("http://localhost:8080/auth/google/callback", "_self");
  };
  

  return (
    <div className="login-page">
      <h2 style={{ textAlign: 'center' }}>Connexion</h2>
      <p>veuiller s'authetifier </p>   
      <div className="form">
        <form className="login-form">
          <label>Email:</label> 
          <input type="email" 
           name="email" 
           placeholder='Entre votre Email' 
           value={email}
           onChange={(e) => setEmail(e.target.value)} />

          <label>Mot de passe:</label>
          <input type="password" 
           placeholder='Entre votre mot de passe '
           value={motPasse}
           onChange={(e) => setMotPasse(e.target.value)} />

          <button type="button" onClick={handleLogin}>
            Connexion 
          </button>

          <p className='message'>Vous n'avez pas de compte ? <Link to="/signup">Sign Up</Link></p>
        </form>

        <button className='login-with-google-btn' 
        onClick={loginWithGoogle}>
        Se connecter avec Google
        </button>

      </div>
    </div>
  );
};

export default Login;
