// SignUp.jsx

import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './SignUp.css'; // Import the CSS file

const SignUp = () => {
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    nom: '',
    prenom: '',
    email: '',
    mot_passe: '',
    date_naissance: '',
    Nbphone: '',
  });

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Send the request to the backend
      const response = await axios.post('http://localhost:8080/register', formData);

      if (response.data.status === 'ok') {
        // Redirect the user after successful registration
        navigate('/');
      } else {
        // Handle registration errors
        console.error('Registration error:', response.data.message);
      }
    } catch (error) {
      console.error('Registration error:', error.response ? error.response.data.message : error.message);
    }
  };

  return (
    <div className="signup-container"> {/* Add a container with a class or id */}
      <h2>Inscription</h2>
      <form onSubmit={handleSubmit}>
        {/* ... rest of your form content */}
        <button type="submit">S'inscrire</button>
      </form>
    </div>
  );
};

export default SignUp;
