import React, { useState, useEffect } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import axios from 'axios';
import './Login.css';

const Login = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [mot_passe, setMotPasse] = useState('');
  const [error, setError] = useState(null);
  const handleSubmit = async (e) => {
  e.preventDefault();

  const requestData = {
    email: email,
    mot_passe: mot_passe,
  };

  // Log the requestData before making the request
  console.log('Request Data:', requestData);

  try {
    const response = await axios.post('http://localhost:8080/loginuser', requestData, {
      withCredentials: true,
    });

    console.log('Response:', response);

    const user = response.data.data.user;

    if (user && user.role === 'admin') {
      console.log('Redirecting to /admin');
      navigate('/admin');
    } else if (user && user.role === 'user') {
      console.log('Redirecting to /dashboard');
      navigate('/dashboard');
    } else {
      console.log('Unknown user or role:', user);
    }
  } catch (error) {
    console.error('Login failed', error);
    console.log('Response Data:', error.response.data);
  }
};


  const loginWithGoogle = () => {
    window.open('http://localhost:8080/auth/google/callback', '_self');
  };
  useEffect(() => {
    const token = 'mLjaK5E6GWwhSv6bSTBCZ0fwa5nphxQOwGLSMOadK5g=';
  
    // Make sure the URL matches your server route
    axios.get('http://localhost:8080/protected-route', {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then(response => {
        console.log('Success:', response.data);
        navigate('/dashboard');
      })
      .catch(error => {
        console.error('Error:', error);
      });
  }, [navigate]);
  
  return (
    <div className="login-page">
      <h2 style={{ textAlign: 'center' }}>Connexion</h2>
      <p>veuillez vous authentifier </p>
      <div className="form">
        {error && <p style={{ color: 'red' }}>{error}</p>}
        <form className="login-form" onSubmit={handleSubmit}>
          <label>Email:</label>
          <input
            type="email"
            name="email"
            placeholder="Entrez votre Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />

          <label>Mot de passe:</label>
          <input
            type="password"
            placeholder="Entrez votre mot de passe "
            value={mot_passe}
            onChange={(e) => setMotPasse(e.target.value)}
          />

          <button type="submit">Connexion</button>
        </form>
        <p className="message">
          Vous n&apos;avez pas de compte ? <Link to="/signup">Sign Up</Link>
        </p>

        <button className="login-with-google-btn" onClick={loginWithGoogle}>
          Se connecter avec Google
        </button>
      </div>
    </div>
  );
};

export default Login;
