import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './SignUp.css';

const SignUp = () => {
  const navigate = useNavigate();
  const [showVerificationDialog, setShowVerificationDialog] = useState(false);

  const [formData, setFormData] = useState({
    nom: '',
    prenom: '',
    email: '',
    mot_passe: '',
    date_naissance: '',
    Nbphone: '',
  });

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost:8080/register', formData);

      if (response.data.status === 'ok') {
        setShowVerificationDialog(true);
        navigate('/login');
      }
    } catch (error) {
      console.error('Erreur d\'inscription:', error.response ? error.response.data.message : error.message);
    }
  };

  return (
    <div className='signup_page '>
      <h2 style={{ textAlign: 'center' }} >Inscription</h2>
      <form onSubmit={handleSubmit}>
        {/* Vos champs de formulaire ici */}
        <button type="submit">S&apos;inscrire</button>
      </form>

      {showVerificationDialog && (
        <div className="verification-dialog">
          <p>Vérifiez votre compte.</p>
          <p>Un e-mail de confirmation a été envoyé à votre adresse e-mail.</p>
        </div>
      )}
    </div>
  );
};

export default SignUp;
